import React from 'react';
import styles from './Board.module.css';
import Cell from './Cell';

const Board = ({ board, openCell, toggleFlag, gameState }) => {
  if (!board || board.length === 0 || !gameState) {
    return null; // Or a loading spinner, or some placeholder
  }

  const boardStyle = {
    gridTemplateColumns: `repeat(${gameState.cols}, var(--cell-size))`,
    gridAutoRows: `var(--cell-size)`,
    gap: '2px',
  };

  const handleCellClick = (row, col) => {
    if (gameState.currentState === 'inProgress') {
      openCell(row, col);
    }
  };

  const handleContextMenu = (e, row, col) => {
    e.preventDefault();
    if (gameState.currentState === 'inProgress') {
      toggleFlag(row, col);
    }
  };

  return (
    <div className={styles.gameBoard} style={boardStyle}>
      {board.map((rowArr, rowIndex) =>
        rowArr.map((cell, colIndex) => (
          <Cell
            key={`${rowIndex}-${colIndex}`}
            cell={cell}
            onClick={() => handleCellClick(rowIndex, colIndex)}
            onContextMenu={(e) => handleContextMenu(e, rowIndex, colIndex)}
            row={rowIndex}
            col={colIndex}
          />
        ))
      )}
    </div>
  );
};

export default Board;
