import React from 'react';
import styles from './Cell.module.css';

const Cell = ({ cell, onClick, onContextMenu, row, col }) => {
  let cellContent = '';
  let cellClasses = [styles.cell];

  if (cell.state === 'open') {
    cellClasses.push(styles.open);
    if (cell.hasMine) {
      cellClasses.push(styles.bomb);
      cellContent = 'ðŸ’£';
    } else if (cell.neighborMines > 0) {
      cellClasses.push(styles[`num${cell.neighborMines}`]);
      cellContent = cell.neighborMines;
    }
  } else if (cell.state === 'flagged') {
    cellClasses.push(styles.flag);
    cellContent = 'ðŸš©';
  }

  return (
    <div
      className={cellClasses.join(' ')}
      onClick={onClick}
      onContextMenu={onContextMenu}
      data-row={row}
      data-col={col}
      title={`r:${row} c:${col}`}
    >
      {cellContent}
    </div>
  );
};

export default Cell;
